import React from "react";
import { BrowserRouter, Routes, Route, Navigate, useLocation } from "react-router-dom";
import AdminLogin from "./pages/AdminLogin";
import AdminDashboard from "./pages/Dashboard";
import AdminClients from "./pages/Clients";
import AdminStats from "./pages/AdminStates";
import AdminNavbar from "./components/AdminNavbar";
import { Shield } from "lucide-react";

// ✅ Private Route Wrapper
function PrivateRoute({ children }) {
  const token = localStorage.getItem("token");
  const location = useLocation();

  if (!token) {
    return <Navigate to="/admin/login" state={{ from: location }} replace />;
  }
  return children;
}

// ✅ Layout for Admin Pages (only shown when logged in)
function AdminLayout({ children }) {
  return (
    <div className="min-h-screen bg-gray-50">
      <AdminNavbar />
      <main className="pt-16 px-4">{children}</main>
    </div>
  );
}


export default function App() {
  const token = localStorage.getItem("token");

  return (
    <BrowserRouter>
      <Routes>
        {/* ✅ Public Route: Login (no navbar or layout) */}
        <Route
          path="/admin/login"
          element={
            token ? (
              <Navigate to="/admin/dashboard" replace />
            ) : (
              <LoginLayout>
                <AdminLogin />
              </LoginLayout>
            )
          }
        />

        {/* ✅ Protected Routes: show navbar + layout */}
        <Route
          path="/admin/dashboard"
          element={
            <PrivateRoute>
              <AdminLayout>
                <AdminDashboard />
              </AdminLayout>
            </PrivateRoute>
          }
        />
        <Route
          path="/admin/clients"
          element={
            <PrivateRoute>
              <AdminLayout>
                <AdminClients />
              </AdminLayout>
            </PrivateRoute>
          }
        />
        <Route
          path="/admin/stats"
          element={
            <PrivateRoute>
              <AdminLayout>
                <AdminStats />
              </AdminLayout>
            </PrivateRoute>
          }
        />

        {/* ✅ Default Redirects */}
        <Route path="/" element={<Navigate to="/admin/login" replace />} />
        <Route path="*" element={<Navigate to="/admin/login" replace />} />
      </Routes>
    </BrowserRouter>
  );
}
