import React, { useEffect, useState } from "react";
import { API } from "../utils/api";
import {
  Plus,
  Minus,
  ToggleLeft,
  ToggleRight,
  Search,
  Loader2,
  AlertCircle,
  UserCheck,
  UserX,
  Wallet,
  RefreshCw,
  X
} from "lucide-react";

export default function AdminClients() {
  const [clients, setClients] = useState([]);
  const [filteredClients, setFilteredClients] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [search, setSearch] = useState("");
  const [modal, setModal] = useState({ open: false, type: "", client: null, amount: "" });
  const [processing, setProcessing] = useState(false);

  // Load clients
  const loadClients = async () => {
    setLoading(true);
    setError("");
    try {
      const res = await API.get("/admin/clients");
      setClients(res.data);
      setFilteredClients(res.data);
    } catch (err) {
      setError("Failed to load clients.");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadClients();
  }, []);

  // Search filter
  useEffect(() => {
    const filtered = clients.filter(
      (c) =>
        c.name.toLowerCase().includes(search.toLowerCase()) ||
        c.email.toLowerCase().includes(search.toLowerCase())
    );
    setFilteredClients(filtered);
  }, [search, clients]);

  // Toggle status
  const toggleStatus = async (id, current) => {
    setProcessing(true);
    try {
      await API.put(`/admin/client/${id}/status`, { status: !current });
      loadClients();
    } catch (err) {
      setError("Failed to update status.");
    } finally {
      setProcessing(false);
    }
  };

  // Open modal
  const openModal = (type, client) => {
    setModal({ open: true, type, client, amount: "" });
    setError("");
  };

  // Handle money action
  const handleMoney = async () => {
    const amount = parseFloat(modal.amount);
    if (!amount || amount <= 0) {
      setError("Enter a valid amount");
      return;
    }

    setProcessing(true);
    try {
      const endpoint =
        modal.type === "add"
          ? `/admin/client/${modal.client._id}/add`
          : `/admin/client/${modal.client._id}/withdraw`;

      await API.post(endpoint, { amount });
      setModal({ ...modal, open: false, amount: "" });
      loadClients();
    } catch (err) {
      setError(err.response?.data?.message || "Transaction failed");
    } finally {
      setProcessing(false);
    }
  };

  return (
    <>
      <div className="min-h-screen bg-gradient-to-br from-slate-50 to-indigo-50 p-4 md:p-8">
        <div className="max-w-7xl mx-auto">

          {/* Header */}
          <div className="flex flex-col md:flex-row justify-between items-start md:items-center mb-8 gap-4">
            <div>
              <h1 className="text-3xl md:text-4xl font-bold bg-gradient-to-r from-purple-600 to-pink-600 bg-clip-text text-transparent">
                Client Management
              </h1>
              <p className="text-gray-600 mt-1">Manage all users and their wallets</p>
            </div>
            <button
              onClick={loadClients}
              className="flex items-center gap-2 px-4 py-2 bg-purple-600 text-white rounded-xl hover:bg-purple-700 transition-all hover:shadow-lg"
            >
              <RefreshCw className={`w-4 h-4 ${loading ? "animate-spin" : ""}`} />
              Refresh
            </button>
          </div>

          {/* Search */}
          <div className="mb-6">
            <div className="relative max-w-md">
              <Search className="absolute left-4 top-1/2 transform -translate-y-1/2 w-5 h-5 text-gray-400" />
              <input
                type="text"
                value={search}
                onChange={(e) => setSearch(e.target.value)}
                placeholder="Search by name or email..."
                className="w-full pl-12 pr-4 py-3 bg-white/80 backdrop-blur-sm border border-gray-200 rounded-xl focus:ring-2 focus:ring-purple-500 focus:border-transparent outline-none transition"
              />
            </div>
          </div>

          {/* Loading */}
          {loading && (
            <div className="bg-white/80 backdrop-blur-lg rounded-3xl shadow-xl p-12 text-center border border-white/20">
              <Loader2 className="w-12 h-12 animate-spin text-purple-600 mx-auto" />
              <p className="text-gray-600 mt-4">Loading clients...</p>
            </div>
          )}

          {/* Error */}
          {error && !loading && (
            <div className="bg-red-50 border border-red-200 text-red-700 px-6 py-4 rounded-2xl flex items-center gap-3">
              <AlertCircle className="w-6 h-6" />
              <p className="font-medium">{error}</p>
            </div>
          )}

          {/* Desktop Table */}
          <div className="hidden md:block bg-white/80 backdrop-blur-lg rounded-3xl shadow-xl overflow-hidden border border-white/20">
            <table className="w-full">
              <thead>
                <tr className="bg-gradient-to-r from-purple-100 to-pink-100 text-gray-700">
                  <th className="py-5 px-6 text-left font-semibold">Client</th>
                  <th className="py-5 px-6 text-left font-semibold">Email</th>
                  <th className="py-5 px-6 text-left font-semibold">Balance</th>
                  <th className="py-5 px-6 text-left font-semibold">Status</th>
                  <th className="py-5 px-6 text-left font-semibold">Actions</th>
                </tr>
              </thead>
              <tbody>
                {filteredClients.length === 0 ? (
                  <tr>
                    <td colSpan="5" className="text-center py-12 text-gray-500">
                      No clients found
                    </td>
                  </tr>
                ) : (
                  filteredClients.map((c) => (
                    <tr
                      key={c._id}
                      className="border-t border-gray-100 hover:bg-purple-50/50 transition-all"
                    >
                      <td className="py-5 px-6">
                        <div className="flex items-center gap-3">
                          <div className="w-10 h-10 bg-gradient-to-br from-purple-400 to-pink-400 rounded-full flex items-center justify-center text-white font-bold">
                            {c.name.charAt(0)}
                          </div>
                          <span className="font-medium">{c.name}</span>
                        </div>
                      </td>
                      <td className="py-5 px-6 text-gray-600">{c.email}</td>
                      <td className="py-5 px-6">
                        <span className="font-bold text-lg text-green-600">
                          ₹{c.balance.toLocaleString()}
                        </span>
                      </td>
                      <td className="py-5 px-6">
                        <button
                          onClick={() => toggleStatus(c._id, c.status)}
                          disabled={processing}
                          className={`flex items-center gap-2 px-3 py-1 rounded-full text-sm font-medium transition-all ${
                            c.status
                              ? "bg-green-100 text-green-700 hover:bg-green-200"
                              : "bg-red-100 text-red-700 hover:bg-red-200"
                          }`}
                        >
                          {c.status ? (
                            <>
                              <UserCheck className="w-4 h-4" /> Active
                            </>
                          ) : (
                            <>
                              <UserX className="w-4 h-4" /> Inactive
                            </>
                          )}
                        </button>
                      </td>
                      <td className="py-5 px-6">
                        <div className="flex items-center gap-3">
                          <button
                            onClick={() => openModal("add", c)}
                            className="flex items-center gap-1 px-3 py-1 bg-green-100 text-green-700 rounded-lg hover:bg-green-200 transition"
                          >
                            <Plus className="w-4 h-4" /> Add
                          </button>
                          <button
                            onClick={() => openModal("withdraw", c)}
                            className="flex items-center gap-1 px-3 py-1 bg-yellow-100 text-yellow-700 rounded-lg hover:bg-yellow-200 transition"
                          >
                            <Minus className="w-4 h-4" /> Withdraw
                          </button>
                        </div>
                      </td>
                    </tr>
                  ))
                )}
              </tbody>
            </table>
          </div>

          {/* Mobile Cards */}
          <div className="md:hidden space-y-4">
            {filteredClients.map((c) => (
              <div
                key={c._id}
                className="bg-white/80 backdrop-blur-lg rounded-3xl shadow-lg p-6 border border-white/20"
              >
                <div className="flex justify-between items-start mb-4">
                  <div className="flex items-center gap-3">
                    <div className="w-12 h-12 bg-gradient-to-br from-purple-400 to-pink-400 rounded-full flex items-center justify-center text-white font-bold text-lg">
                      {c.name.charAt(0)}
                    </div>
                    <div>
                      <p className="font-semibold text-lg">{c.name}</p>
                      <p className="text-sm text-gray-600">{c.email}</p>
                    </div>
                  </div>
                  <button
                    onClick={() => toggleStatus(c._id, c.status)}
                    className={`p-2 rounded-full ${c.status ? "bg-green-100" : "bg-red-100"}`}
                  >
                    {c.status ? (
                      <UserCheck className="w-5 h-5 text-green-600" />
                    ) : (
                      <UserX className="w-5 h-5 text-red-600" />
                    )}
                  </button>
                </div>

                <div className="flex justify-between items-center mb-4">
                  <span className="text-sm text-gray-600">Balance</span>
                  <span className="font-bold text-xl text-green-600">
                    ₹{c.balance.toLocaleString()}
                  </span>
                </div>

                <div className="flex gap-2">
                  <button
                    onClick={() => openModal("add", c)}
                    className="flex-1 flex items-center justify-center gap-2 py-2 bg-green-100 text-green-700 rounded-xl hover:bg-green-200 transition"
                  >
                    <Plus className="w-4 h-4" /> Add
                  </button>
                  <button
                    onClick={() => openModal("withdraw", c)}
                    className="flex-1 flex items-center justify-center gap-2 py-2 bg-yellow-100 text-yellow-700 rounded-xl hover:bg-yellow-200 transition"
                  >
                    <Minus className="w-4 h-4" /> Withdraw
                  </button>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* Modal */}
      {modal.open && (
        <div className="fixed inset-0 bg-black/50 backdrop-blur-sm flex items-center justify-center p-4 z-50 animate-fadeIn">
          <div className="bg-white rounded-3xl shadow-2xl w-full max-w-md p-8 relative transform transition-all animate-scaleIn">
            <button
              onClick={() => setModal({ ...modal, open: false })}
              className="absolute top-4 right-4 text-gray-400 hover:text-gray-600"
            >
              <X className="w-6 h-6" />
            </button>

            <div className="text-center mb-6">
              <div className="inline-flex p-3 bg-purple-100 rounded-full mb-4">
                {modal.type === "add" ? (
                  <Plus className="w-8 h-8 text-purple-600" />
                ) : (
                  <Minus className="w-8 h-8 text-red-600" />
                )}
              </div>
              <h2 className="text-2xl font-bold text-gray-800">
                {modal.type === "add" ? "Add Money" : "Withdraw Money"}
              </h2>
              <p className="text-gray-600 mt-1">
                Client: <span className="font-bold">{modal.client.name}</span>
              </p>
            </div>

            <div className="space-y-4">
              <input
                type="number"
                value={modal.amount}
                onChange={(e) => setModal({ ...modal, amount: e.target.value })}
                className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-purple-500 focus:border-transparent outline-none"
                placeholder="0.00"
                min="1"
                step="0.01"
              />

              {error && (
                <div className="flex items-center gap-2 text-red-600 bg-red-50 px-4 py-3 rounded-lg">
                  <AlertCircle className="w-5 h-5" />
                  <span className="text-sm">{error}</span>
                </div>
              )}

              <button
                onClick={handleMoney}
                disabled={processing || !modal.amount}
                className={`w-full py-3 rounded-xl font-semibold text-white flex items-center justify-center gap-2 transition-all ${
                  processing || !modal.amount
                    ? "bg-gray-400 cursor-not-allowed"
                    : modal.type === "add"
                    ? "bg-gradient-to-r from-green-500 to-emerald-600 hover:shadow-lg"
                    : "bg-gradient-to-r from-red-500 to-rose-600 hover:shadow-lg"
                }`}
              >
                {processing ? (
                  <Loader2 className="w-5 h-5 animate-spin" />
                ) : (
                  <>
                    <Wallet className="w-5 h-5" />
                    <span>{modal.type === "add" ? "Add" : "Withdraw"} ₹{modal.amount || "0"}</span>
                  </>
                )}
              </button>
            </div>
          </div>
        </div>
      )}
    </>
  );
}
