import React, { useEffect, useState } from "react";
import { API } from "../utils/api";

export default function AdminStats() {
  const [stats, setStats] = useState({});

  useEffect(() => {
    API.get("/admin/stats").then((res) => setStats(res.data));
  }, []);

  return (
    <div className="p-6">
      <h2 className="text-xl font-bold mb-4">System Stats</h2>

      <div className="grid grid-cols-3 gap-4">
        <div className="bg-blue-100 p-4 rounded-lg">
          <p className="text-gray-600">Total Deposits</p>
          <h3 className="text-2xl font-bold">₹{stats.totalDeposits}</h3>
        </div>
        <div className="bg-red-100 p-4 rounded-lg">
          <p className="text-gray-600">Total Withdrawals</p>
          <h3 className="text-2xl font-bold">₹{stats.totalWithdraws}</h3>
        </div>
        <div className="bg-green-100 p-4 rounded-lg">
          <p className="text-gray-600">Total Balance</p>
          <h3 className="text-2xl font-bold">₹{stats.totalBalance}</h3>
        </div>
      </div>
    </div>
  );
}
