import React, { useEffect, useState } from "react";
import { API } from "../utils/api";
import { PlusCircle, MinusCircle, ToggleLeft, ToggleRight } from "lucide-react";

export default function AdminClients() {
  const [clients, setClients] = useState([]);

  async function loadClients() {
    const res = await API.get("/admin/clients");
    setClients(res.data);
  }

  useEffect(() => {
    loadClients();
  }, []);

  async function toggleStatus(id, current) {
    await API.put(`/admin/client/${id}/status`, { status: !current });
    loadClients();
  }

  async function addMoney(id) {
    const amount = prompt("Enter amount to add:");
    if (!amount) return;
    await API.post(`/admin/client/${id}/add`, { amount });
    loadClients();
  }

  async function withdrawMoney(id) {
    const amount = prompt("Enter amount to withdraw:");
    if (!amount) return;
    await API.post(`/admin/client/${id}/withdraw`, { amount });
    loadClients();
  }

  return (
    <div className="p-6">
      <h2 className="text-xl font-bold mb-4">All Clients</h2>

      <table className="w-full border-collapse bg-white rounded-lg shadow overflow-hidden">
        <thead className="bg-gray-100">
          <tr className="text-left">
            <th className="p-3">Name</th>
            <th className="p-3">Email</th>
            <th className="p-3">Balance</th>
            <th className="p-3">Status</th>
            <th className="p-3">Actions</th>
          </tr>
        </thead>
        <tbody>
          {clients.map((c) => (
            <tr key={c._id} className="border-t hover:bg-gray-50">
              <td className="p-3">{c.name}</td>
              <td className="p-3">{c.email}</td>
              <td className="p-3">â‚¹{c.balance}</td>
              <td className="p-3">
                {c.status ? (
                  <span className="text-green-600 flex items-center gap-1">
                    <ToggleRight size={18} /> Active
                  </span>
                ) : (
                  <span className="text-red-600 flex items-center gap-1">
                    <ToggleLeft size={18} /> Inactive
                  </span>
                )}
              </td>
              <td className="p-3 flex gap-3">
                <button
                  onClick={() => addMoney(c._id)}
                  className="text-green-600 hover:text-green-800 flex items-center gap-1"
                >
                  <PlusCircle size={18} /> Add
                </button>
                <button
                  onClick={() => withdrawMoney(c._id)}
                  className="text-yellow-600 hover:text-yellow-800 flex items-center gap-1"
                >
                  <MinusCircle size={18} /> Withdraw
                </button>
                <button
                  onClick={() => toggleStatus(c._id, c.status)}
                  className="text-blue-600 hover:text-blue-800"
                >
                  Toggle
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
